import { useState } from "react";
import { useDispatch } from "react-redux";
import { action } from "../store";

function Login() {
  const [login, setLogin] = useState({ user: "", pass: "" });
  const [loginmsg, setLoginMsg] = useState("");

  const dispatch = useDispatch();

  let checkLogin = (event) => {
    event.preventDefault();
    if (login.user === "admin" && login.pass === "password") {
      setLoginMsg("Login Successful");
      dispatch(action.changeLogin(true));
      dispatch(
        action.changeUser({
          username: login.user,
          password: login.password,
        })
      );
    } else {
      setLoginMsg("Invalid Username or Password! Try again !!!");
      dispatch(action.changeLogin(false));
    }
  };

  return (
    <div>
      <form onSubmit={checkLogin} className="vh-100">
        <div className="container py-5 h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col col-xl-10">
              <div className="card">
                <div className="row g-0">
                  <h3 class=" h1 fw-bold mb-3 pb-3">Sign into your account</h3>{" "}
                  <div className="form-outline mb-4">
                    <label className="h3 fw-normal mb-0">Username</label>
                    <input
                      className="form-control form-control-lg "
                      type="text"
                      id="user"
                      onBlur={(e) => {
                        setLogin({
                          user: e.target.value,
                          pass: login.pass,
                        });
                      }}
                      autoFocus
                    />
                  </div>
                  <div className="form-outline mb-4">
                    <label className="h3 fw-normal mb-0">Password</label>
                    <input
                      className="form-control form-control-lg mb-4"
                      type="password"
                      id="pass"
                      onBlur={(e) => {
                        setLogin({
                          user: login.user,
                          pass: e.target.value,
                        });
                      }}
                    />
                    <button
                      className="btn btn-dark btn-lg btn-block"
                      type="submit"
                    >
                      Login
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </form>
      <div>{loginmsg}</div>
    </div>
  );
}

export default Login;
