import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { actions } from "../store";

const Counter = () => {
  const counter = useSelector((state) => state.counter);
  const student = useSelector((state) => state.student);
  const dispatch = useDispatch();
  const increment = () => {
    dispatch(actions.increment());
  };
  const decrement = () => {
    dispatch(actions.decrement());
  };
  const [inc, setInc] = useState(0);
  const byinc = () => {
    dispatch(actions.incrementBy({ payload: inc }));
  };

  return (
    <div className="container">
      <h1>Counter Value : {counter}</h1>
      <div>
        <button type="button" className="btn" onClick={increment}>
          Increment
        </button>
        <button type="button" className="btn" onClick={decrement}>
          Decrement
        </button>
      </div>
      <div>
        <form action="">
          <label htmlFor="">Enter Value to Increment</label>
          <br />
          <input
            type="text"
            id="input1"
            onBlur={(e) => {
              setInc(parseInt(e.target.value));
            }}
          />
          <br />
          <button type="button" className="btn" onClick={byinc}>
            Add
          </button>
        </form>
      </div>
      <div className="stdata">
        <p>Name:{student.name}</p>
        <p>ID:{student.id}</p>
      </div>
    </div>
  );
};

export default Counter;
