import React, { useState } from "react";

function SelectProduct() {
  const [plist, setPlist] = useState([
    {
      id: "1001",
      name: "Colgate 100gm",
      cost: "20",
      quantity: 0,
    },
    {
      id: "1002",
      name: "Rice 1kg",
      cost: "150",
      quantity: 0,
    },
    // ... other products ...
  ]);

  const handleIncrement = (productIndex) => {
    setPlist((prevList) => {
      const updatedList = [...prevList];
      updatedList[productIndex].quantity += 1;
      return updatedList;
    });
  };

  const handleDecrement = (productIndex) => {
    setPlist((prevList) => {
      const updatedList = [...prevList];
      if (updatedList[productIndex].quantity > 0) {
        updatedList[productIndex].quantity -= 1;
      }
      return updatedList;
    });
  };

  const prepareList = () => {
    return plist.map((product, index) => (
      <div key={product.id}>
        <li className="list-group-item">
          <div>{product.id}</div>
          <div className="fs-1">{product.name}</div>
          <div>{product.cost}</div>
          <div>{product.quantity}</div>
          <div>
            <button
              className="btn btn-primary"
              onClick={() => handleIncrement(index)}
            >
              +
            </button>
            <button
              className="btn btn-primary"
              onClick={() => handleDecrement(index)}
            >
              -
            </button>
          </div>
        </li>
      </div>
    ));
  };

  return (
    <div>
      <ul className="list-group">{prepareList()}</ul>
    </div>
  );
}

export default SelectProduct;
